import React, { useContext, useEffect, useState } from 'react'
import CartContext from '../../store/CartContextProvider'

const Toast = ({user}) => {
const{msg,setMsgg}=useContext(CartContext);

  console.log(msg)


  useEffect(()=>{
    console.log("before")
    setMsgg(msg)
    console.log("after")
   },[])
   if(msg===""){
    return
   }


  return (


    <div class='pt-3  '>
      <div class="alert alert-success alert-dismissible fade show container-sm mt-5 w-50 " role="alert">
  <strong className='text-center'>{msg}</strong>
  <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
</div>
    </div>


  )
}

export default Toast
