import 'react-toastify/dist/ReactToastify.css';

const Layout = (props) => {
  const { loginWithRedirect } = useAuth0();
  const { logout,isAuthenticated,user } = useAuth0();


  return (
    <Fragment>
        <header>

         <MAinNav/>


        </header>

        <main style={{marginTop:'10.5rem'}}>





           {props.children}
        </main>
        <footer>
       {isAuthenticated && <ToastContainer
position="top-center"
autoClose={5000}
hideProgressBar={false}
newestOnTop={false}
closeOnClick
rtl={false}
