import React, { useContext,useRef } from "react";
import CartContext from "../store/CartContextProvider";
import styles from "./MyCart.module.css";
import axios from "axios";


const MyCart = () => {
  const { cart, clearCart,deleteItem,setMsg,addToCart } = useContext(CartContext);
  const totalPrice = cart.reduce((sum, item) => sum + item.price * parseInt(item.qty), 0);
  const qtyref=useRef();

  const placeOrderHandler = async () => {
    try {
      const res = await axios.post("http://localhost:8000/placeOrder", {
        cart,
      });
      if (res.status !== 200) {
        setMsg("Cannot Place The Order");
        throw new Error("cannot place the order");
      }
      clearCart();
      setMsg(" Order Placed Successfully");
    } catch (err) {
      setMsg("Something Went Wrong");
    }
  };

  const ClearCartHandler=()=>{

    clearCart();
    setMsg("Cart Cleared Successfully")
  }


  return (
    <ul className={styles.cart}>
      <li>My Cart</li>
      {cart.map((item, idx) => {

        return (
          <li key={idx}>
            <p className={styles["item-name"]}>
              {item.name}{" "}
              <span className={styles["item-qty"]} ref={qtyref} >x {item.qty}  </span>

              <button onClick={()=>{
                console.log(qtyref.current.innerText)
                  addToCart({
      id: item.id,
      name: item.name,
      price: item.price,
      desc: item.desc,
      qty: qtyref.current.value+1
    });
              }}  className="p-1 mx-2">+</button> 
              <button className="p-1 mx-2">-</button> 
             </p>
            <p className={styles["item-desc"]}>{item.desc}</p>
            <p className={styles["item-price"]}>$ {item.price}</p>
            <button onClick={()=>{ deleteItem(item.id);
             setMsg("Deleted Item Successfully")}}>Delete</button>
          </li>
        );
      })}
      <li>
        Total:{(Math.round(totalPrice * 100) / 100).toFixed(2)}{" "}
        <span className={styles["place-order-btn"] }>
          <button onClick={ClearCartHandler}>Clear Cart</button>
        </span>
        <span className={styles["place-order-btn"]}>
          <button onClick={placeOrderHandler}>Place Order</button>
        </span>{" "}
      </li>
    </ul>
  );
};

export default MyCart;
